```js
var throttle = function(fn, timeout, delay) {
    var timer = null,
        last = null;
    return function() {
        var curr = Date.now(),
            context = this,
            args = arguments;
        if (!last) last = Date.now();
        if (curr - last >= timeout) {
            clearTimeout(timer);
            timer = null;
            last = curr;
            fn.apply(context, args);
        } else {
            timer && clearTimeout(timer);
            timer = setTimeout(function() {
                fn.apply(this, arguments);
            }, delay);
        }
    };
};
```

