var throttle = function(fn, internal) {
    var __self = fn,
        timer;
    return function() {
        var args = arguments,
            __me = this;

        if (timer) {
            return false;
        }

        timer = setTimeout(function() {
            clearTimeout(timer);
            timer = null;
            __self.apply(__me, args);
        }, interval || 500);
    }
}
```

