```js
function asyncify(fn) {
    var oldFn = fn,
        timer = setTimeout(() => {
            timer = null;
            fn && fn();
        }, 0);
    return function(...args) {
        if (timer) {
            fn = oldFn.bind.apply(oldFn, [this, ...args]);
        } else {
            oldFn.apply(this, args);
        }
    }
}

