        'use strict' // javascript

        import React, { Component } from 'react';
        import { bindActionCreators } from 'redux';
        import Discolor from '../components/discolor';
        import * as discolorActions from '../actions/discolorActions';
        import { connect } from 'react-redux';

        class DiscolorApp extends Component {
          constructor(props) {
            super(props);
          }

          render() {
            const { state, actions } = this.props;
            return (
              <Discolor
                color={state.color}
                {...actions} />
            );
          }
        }

        /*
         *
         * connect([mapStateToProps], [mapDispatchToProps], [mergeProps], [options])
         *
         * http://cn.redux.js.org/docs/react-redux/api.html
         *
         * [mapStateToProps(state, [ownProps]): stateProps] (Function):  Redux store 
         *  Redux store mapStateToProps 
         *  props  Redux store
         *  ownProps props propsmapStateToProps 
         *
         * state: state.discolordiscolorreducers/discolor.js
         * Redux storediscolordiscolorstate
         * discolorstatereducer
         */
        export default connect(state => ({
            state: state.discolor
          }),
          (dispatch) => ({
            /*
             *  bindActionCreators :  action creators  keys  dispatch  action creator 
             * bindActionCreators
             *   {
             *     change2red: dispatch(discolorActions.change2red),
             *     change2green: dispatch(discolorActions.change2green),
             *   }
             */
            actions: bindActionCreators(discolorActions, dispatch),
          })
        )(DiscolorApp);

