        import * as types from '../actions/actionTypes';

        const initialState = {
          color: 'red',
        };

        export default function discolor(state = initialState, action = {}) {
          switch (action.type) {
            case types.CHANGE2RED:
              return {
                ...state,
                color: 'red',
              };
            case types.CHANGE2GREEN:
              return {
                ...state,
                color: 'green',
              };
            default:
              return state;
          }
        }
